package com.androidtechguru.blueapp.diimport com.androidtechguru.blueapp.domain.repository.DataRepositoryimport com.androidtechguru.blueapp.domain.usecase.GetImagesUseCaseimport com.androidtechguru.blueapp.domain.usecase.GetItemsUseCaseimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.components.ViewModelComponent@Module@InstallIn(ViewModelComponent::class)object ViewModelModule {    @Provides    fun provideGetImagesUseCase(        itemRepository: DataRepository    ): GetImagesUseCase = GetImagesUseCase(itemRepository)    @Provides    fun provideGetItemsUseCase(        itemRepository: DataRepository    ): GetItemsUseCase = GetItemsUseCase(itemRepository)}