package com.androidtechguru.blueapp.domain.usecaseimport com.androidtechguru.blueapp.domain.model.Imageimport com.androidtechguru.blueapp.domain.repository.DataRepositoryimport kotlinx.coroutines.flow.Flowimport javax.inject.Injectclass GetImagesUseCase @Inject constructor(    private val itemRepository: DataRepository) {    suspend operator fun invoke(): Flow<List<Image>> {        return itemRepository.getImages()    }}