package com.androidtechguru.blueapp.domain.usecaseimport com.androidtechguru.blueapp.domain.model.Itemimport com.androidtechguru.blueapp.domain.repository.DataRepositoryimport kotlinx.coroutines.flow.Flowimport javax.inject.Injectclass GetItemsUseCase @Inject constructor(    private val itemRepository: DataRepository) {    suspend operator fun invoke(page: Int): Flow<List<Item>> {        return itemRepository.getItems()    }}