package com.androidtechguru.blueapp.presentation.ui.adapter;import android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.androidtechguru.blueapp.databinding.ItemListBindingimport com.androidtechguru.blueapp.domain.model.Itemimport com.androidtechguru.blueapp.presentation.utils.convertURLToDrawableclass ItemsAdapter(private var items: List<Item>) :    RecyclerView.Adapter<ItemsAdapter.ItemViewHolder>() {    private val originalList: List<Item> = items    private var filteredItems: List<Item> = items    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemViewHolder {        val binding = ItemListBinding.inflate(            LayoutInflater.from(parent.context), parent, false)        return ItemViewHolder(binding)    }    override fun getItemCount(): Int = items.size    override fun onBindViewHolder(holder: ItemViewHolder, position: Int) {        holder.bind(items[position])    }    inner class ItemViewHolder(private val binding: ItemListBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(item: Item) {            binding.itemImage.setImageResource(item.imageUrl.convertURLToDrawable())            binding.itemTitle.text = item.title            binding.itemSubtitle.text = item.subtitle        }    }    fun updateData(newData: List<Item>) {        items = newData        filteredItems = newData        notifyDataSetChanged()    }    fun getFilteredItems(): List<Item> {        return filteredItems    }}