package com.androidtechguru.blueapp.presentation.ui.componentsimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.Composableimport androidx.compose.runtime.getValueimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.runtime.setValueimport androidx.compose.ui.Modifierimport androidx.compose.ui.res.stringResourceimport com.androidtechguru.blueapp.Rimport com.androidtechguru.blueapp.presentation.ui.viewmodel.MainViewModel@Composablefun SearchBar(viewModel: MainViewModel) {    var query by remember { mutableStateOf("") }    val searchHint = stringResource(id = R.string.search_hint)        TextField(        value = query,        onValueChange = {            query = it            viewModel.onSearchQueryChanged(query)        },        modifier = Modifier.fillMaxWidth(),        placeholder = {            Text(text = searchHint)        }    )}